#include <bits/stdc++.h>
using namespace std;
bool anagram(string x,string y){
	if(x.length()!=y.length())
		return false;
    long long int arr[26]={0};
    for(long long int i=0;i<x.length();i++){
        arr[x[i]-'a']++;
    }
    for(long long int i=0;i<y.length();i++){
        arr[y[i]-'a']--;
    }
    
    for(int i=0;i<26;i++){
        if(arr[i]==1)
            return false;
    }
    return true;
}
int main() {
	//code
	int t;
	cin>>t;
	while(t--){
	    string x,y;
	    cin>>x>>y;
	    if(anagram(x,y))
	        cout<<"YES"<<endl;
	    else
	        cout<<"NO"<<endl;
	}
	return 0;
}

 
